import React from 'react';
import { useActorsWithMultipleCharacters } from '../hooks/useActors';

const ActorList = () => {
  const { data: actorsData, loading, error } = useActorsWithMultipleCharacters();

  const renderActors = () => {
    if (!data) return null;

    return (
      <List>
        {Object.entries(data).map(([actorName, characters]) => (
          <Box key={actorName} sx={{ marginBottom: 2 }}>
            <ListSubheader sx={{ bgcolor: 'inherit' }}>{actorName}</ListSubheader>
            <List>
              {characters.map((character, index) => (
                <ListItem key={index}>
                  <strong>{character.characterName}</strong> in {character.movieName}
                </ListItem>
              ))}
            </List>
          </Box>
        ))}
      </List>
    );
  };

  return (
    <Container maxWidth="md">
      <Box sx={{ padding: 2, boxShadow: 3, borderRadius: 2, bgcolor: 'white' }}>
        <Typography variant="h4" gutterBottom>
          Actors with Multiple Characters
        </Typography>

        {loading && <CircularProgress />}
        {error && <Typography color="error">Error: {error}</Typography>}

        {renderActors()}
      </Box>
    </Container>
  );
};

export default ActorList;
