// /src/components/MovieList.tsx
import React, { useState, useEffect } from 'react';
import { fetchMoviesPerActor } from '../api/actorsApi';
import { useMoviePerActor } from '../hooks/useActors';

const MovieList = () => {
    const { data: moviesPerActor, loading, error } = useMoviePerActor();
    const [selectedActor, setSelectedActor] = useState<string | null>(null);

  return (
    <div>
      <h1>Movies Per Actor</h1>
      {loading && <p>Loading...</p>}
      {error && <p>Error: {error}</p>}
      {moviesPerActor && 
        <select onChange={(e) => setSelectedActor(e.target.value)} defaultValue="">
        <option value="">Select Actor</option>
        {moviesPerActor && Object.keys(moviesPerActor).map((actor) => (
        <option key={actor} value={actor}>{actor}</option>
        ))}
        </select>}

      {selectedActor && (
        <div>
          <h2>{selectedActor}</h2>
          <ul>
            {moviesPerActor && moviesPerActor[selectedActor].map((movie, idx) => (
              <li key={idx}>{movie}</li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
};

export default MovieList;
