import React from 'react';
import { useCharactersWithMultipleActors } from '../hooks/useCharacters';
import { Container, Typography, Box, CircularProgress, List, ListItem, ListSubheader } from '@mui/material';

const CharacterList = () => {
  const { data , loading, error } = useCharactersWithMultipleActors();

  const renderCharacters = () => {
    if (!data) return null;

    return (
      <List>
        {Object.entries(data).map(([characterName, actors]) => (
          <Box key={characterName} sx={{ marginBottom: 2 }}>
            <ListSubheader>{characterName}</ListSubheader>
            <List>
              {actors.map((actor, index) => (
                <ListItem key={index}>
                  <strong>{actor.actorName}</strong>in {actor.movieName}
                </ListItem>
              ))}
            </List>
          </Box>
        ))}
      </List>
    );
  };

  return (
    <Container maxWidth="md">
      <Box sx={{ padding: 2, boxShadow: 3, borderRadius: 2, bgcolor: 'white' }}>
        <Typography variant="h4" gutterBottom>
          Characters with Multiple Actors
        </Typography>

        {loading && <CircularProgress />}
        {error && <Typography color="error">Error: {error}</Typography>}

        {renderCharacters()}
      </Box>
    </Container>
  );

};

export default CharacterList;
