// /src/components/MovieList.tsx
import React, { useState, useEffect } from 'react';
import { fetchMoviesPerActor } from '../api/actorsApi';

const MovieList = () => {
  const [moviesPerActor, setMoviesPerActor] = useState<{ [actorName: string]: string[] }>({});
  const [selectedActor, setSelectedActor] = useState<string | null>(null);

  useEffect(() => {
    const getMoviesData = async () => {
      const data = await fetchMoviesPerActor();
      setMoviesPerActor(data);
    };
    getMoviesData();
  }, []);

  return (
    <div>
      <h1>Movies Per Actor</h1>
      <select onChange={(e) => setSelectedActor(e.target.value)} defaultValue="">
        <option value="">Select Actor</option>
        {Object.keys(moviesPerActor).map((actor) => (
          <option key={actor} value={actor}>{actor}</option>
        ))}
      </select>

      {selectedActor && (
        <div>
          <h2>{selectedActor}</h2>
          <ul>
            {moviesPerActor[selectedActor].map((movie, idx) => (
              <li key={idx}>{movie}</li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
};

export default MovieList;
